import React, { useState, useEffect, useCallback, useRef } from 'react';
import { ChevronUp, ChevronDown, ChevronLeft, ChevronRight, Play, Pause, RotateCcw, Volume2, VolumeX } from 'lucide-react';

const GRID_SIZE = 15;
const INITIAL_SNAKE = [{ x: 7, y: 7 }];
const INITIAL_DIRECTION = 'RIGHT';
const INITIAL_SPEED = 150;
const SPEED_INCREMENT = 0.1;

// Barn Foto Snacke Feature Constants
const IMAGE_POPUP_SCORE_INTERVAL = 15;
const NUM_BARN_FOTOS = 21; 
const IMAGE_POPUP_DURATION = 3000; // Total duration the panel is shown

const SNAKE_COLOR_THEMES = [
  { head: 'bg-gradient-to-br from-green-400 to-green-600 ring-4 ring-green-300', body: 'bg-green-500' }, // Cheerful Green
  { head: 'bg-gradient-to-br from-yellow-300 to-yellow-500 ring-4 ring-yellow-200', body: 'bg-yellow-400' }, // Sunny Yellow
  { head: 'bg-gradient-to-br from-sky-400 to-sky-600 ring-4 ring-sky-300', body: 'bg-sky-500' }, // Bright Blue
  { head: 'bg-gradient-to-br from-orange-400 to-orange-600 ring-4 ring-orange-300', body: 'bg-orange-500' }, // Friendly Orange
  { head: 'bg-gradient-to-br from-pink-400 to-pink-600 ring-4 ring-pink-300', body: 'bg-pink-500' }, // Playful Pink
];

const POWER_UPS = {
  PIZZA: { icon: '🍕', points: 1, probability: 0.35, description: "Pizza Slice!" },
  HAMBURGER: { icon: '🍔', points: 1, probability: 0.35, description: "Yummy Burger!" },
  SPEED: { icon: '⚡️', points: 2, probability: 0.1, description: "Zoom Zoom!", color: 'bg-yellow-300', textColor: 'text-yellow-700' }, // Softer yellow
  MULTIPLIER: { icon: '✨', points: 3, probability: 0.1, description: "Super Score!", color: 'bg-pink-300', textColor: 'text-pink-700' }, // Softer pink
  SHIELD: { icon: '🛡️', points: 1, probability: 0.1, description: "Safe Shield!", color: 'bg-sky-300', textColor: 'text-sky-700' } // Softer sky blue
};

const getRandomFoodType = () => {
  const rand = Math.random();
  let cumulativeProbability = 0;
  for (const type in POWER_UPS) {
    cumulativeProbability += POWER_UPS[type].probability;
    if (rand < cumulativeProbability) {
      return type;
    }
  }
  return 'PIZZA'; 
};

const getRandomImageName = () => { 
  if (NUM_BARN_FOTOS === 0) return '';
  const randomIndex = Math.floor(Math.random() * NUM_BARN_FOTOS) + 1;
  return `${randomIndex}.jpg`;
};

const SnakeGame = () => {
  const [snake, setSnake] = useState(INITIAL_SNAKE);
  const [food, setFood] = useState({ x: 10, y: 10, type: getRandomFoodType() });
  const [direction, setDirection] = useState(INITIAL_DIRECTION);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('snakeHighScore');
    return saved ? parseInt(saved) : 0;
  });
  const [isGameOver, setIsGameOver] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [gameSpeed, setGameSpeed] = useState(INITIAL_SPEED);
  const [hasShield, setHasShield] = useState(false);
  const [scoreMultiplier, setScoreMultiplier] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [touchStart, setTouchStart] = useState({ x: 0, y: 0 });
  const [currentThemeIndex, setCurrentThemeIndex] = useState(0);

  const [imagePopupVisible, setImagePopupVisible] = useState(false); 
  const [currentRandomImageName, setCurrentRandomImageName] = useState('');
  const [nextImageScoreThreshold, setNextImageScoreThreshold] = useState(IMAGE_POPUP_SCORE_INTERVAL);

  const gameLoopRef = useRef();
  const audioContextRef = useRef();
  const currentSnakeColor = SNAKE_COLOR_THEMES[currentThemeIndex];

  // Initialize audio context
  useEffect(() => {
    if (!isMuted && !audioContextRef.current) {
      try {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      } catch (e) {
        console.log('Audio not supported');
      }
    }
  }, [isMuted]);

  // Play sound effect
  const playSound = useCallback((frequency, duration = 100) => {
    if (isMuted || !audioContextRef.current) return;
    
    try {
      const oscillator = audioContextRef.current.createOscillator();
      const gainNode = audioContextRef.current.createGain();
      
      oscillator.connect(gainNode);
      gainNode.connect(audioContextRef.current.destination);
      
      oscillator.frequency.setValueAtTime(frequency, audioContextRef.current.currentTime);
      oscillator.type = 'square';
      
      gainNode.gain.setValueAtTime(0.1, audioContextRef.current.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContextRef.current.currentTime + duration / 1000);
      
      oscillator.start(audioContextRef.current.currentTime);
      oscillator.stop(audioContextRef.current.currentTime + duration / 1000);
    } catch (e) {
      console.log('Audio playback failed');
    }
  }, [isMuted]);

  // Generate random food position
  const generateFood = useCallback(() => {
    let newFoodPosition;
    do {
      newFoodPosition = {
        x: Math.floor(Math.random() * GRID_SIZE),
        y: Math.floor(Math.random() * GRID_SIZE),
      };
    } while (snake.some(segment => segment.x === newFoodPosition.x && segment.y === newFoodPosition.y));
    
    return {...newFoodPosition, type: getRandomFoodType()};
  }, [snake]);

  // Handle food consumption
  const handleFoodConsumption = useCallback((foodType) => {
    const basePoints = POWER_UPS[foodType].points;
    const points = basePoints * scoreMultiplier;
    
    setScore(prev => prev + points);
    playSound(440, 150);
    setCurrentThemeIndex(prev => (prev + 1) % SNAKE_COLOR_THEMES.length);
    
    switch (foodType) {
      case 'SPEED':
        setGameSpeed(prev => Math.max(50, prev * 0.8));
        setTimeout(() => setGameSpeed(prev => prev / 0.8), 5000);
        break;
      case 'MULTIPLIER':
        setScoreMultiplier(2);
        setTimeout(() => setScoreMultiplier(1), 8000);
        break;
      case 'SHIELD':
        setHasShield(true);
        setTimeout(() => setHasShield(false), 15000);
        break;
    }
  }, [scoreMultiplier, playSound]);

  // Move snake
  const moveSnake = useCallback(() => {
    if (isPaused || isGameOver) return;

    setSnake(currentSnake => {
      const head = { ...currentSnake[0] };
      
      switch (direction) {
        case 'UP': head.y -= 1; break;
        case 'DOWN': head.y += 1; break;
        case 'LEFT': head.x -= 1; break;
        case 'RIGHT': head.x += 1; break;
      }

      if (head.x < 0 || head.x >= GRID_SIZE || head.y < 0 || head.y >= GRID_SIZE) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      if (currentSnake.some(segment => segment.x === head.x && segment.y === head.y)) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      const newSnake = [head, ...currentSnake];

      if (head.x === food.x && head.y === food.y) {
        handleFoodConsumption(food.type);
        setFood(generateFood());
        return newSnake;
      }

      return newSnake.slice(0, -1);
    });
  }, [direction, isPaused, isGameOver, food, hasShield, handleFoodConsumption, generateFood, playSound]);

  // Game loop
  useEffect(() => {
    if (!isGameOver && !isPaused) {
      gameLoopRef.current = setTimeout(moveSnake, gameSpeed);
    }
    return () => clearTimeout(gameLoopRef.current);
  }, [moveSnake, gameSpeed, isGameOver, isPaused]);

  // Update high score & Barn Foto Snacke popup trigger
  useEffect(() => {
    if (score > highScore) {
      setHighScore(score);
      localStorage.setItem('snakeHighScore', score.toString());
    }

    if (!imagePopupVisible && score > 0 && score >= nextImageScoreThreshold && NUM_BARN_FOTOS > 0) {
      setImagePopupVisible(true);
      setCurrentRandomImageName(getRandomImageName()); // Set a single random image for the popup session
      playSound(660, 200); 
      setNextImageScoreThreshold(prevThreshold => prevThreshold + IMAGE_POPUP_SCORE_INTERVAL);
      
      setTimeout(() => {
        setImagePopupVisible(false);
      }, IMAGE_POPUP_DURATION);
    }
  }, [score, highScore, nextImageScoreThreshold, imagePopupVisible, playSound]);

  // Increase speed based on score
  useEffect(() => {
    const newSpeed = Math.max(50, INITIAL_SPEED - Math.floor(score / 5) * (INITIAL_SPEED * SPEED_INCREMENT));
    setGameSpeed(newSpeed);
  }, [score]);

  // Keyboard controls
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (isGameOver) return;
      
      switch (e.key) {
        case 'ArrowUp': case 'w': case 'W':
          e.preventDefault(); if (direction !== 'DOWN') setDirection('UP'); break;
        case 'ArrowDown': case 's': case 'S':
          e.preventDefault(); if (direction !== 'UP') setDirection('DOWN'); break;
        case 'ArrowLeft': case 'a': case 'A':
          e.preventDefault(); if (direction !== 'RIGHT') setDirection('LEFT'); break;
        case 'ArrowRight': case 'd': case 'D':
          e.preventDefault(); if (direction !== 'LEFT') setDirection('RIGHT'); break;
        case ' ':
          e.preventDefault(); setIsPaused(prev => !prev); break;
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [direction, isGameOver]);

  // Touch controls
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setTouchStart({ x: touch.clientX, y: touch.clientY });
  };

  const handleTouchEnd = (e) => {
    if (isGameOver) return;
    const touch = e.changedTouches[0];
    const deltaX = touch.clientX - touchStart.x;
    const deltaY = touch.clientY - touchStart.y;
    const minSwipeDistance = 30;
    if (Math.abs(deltaX) > Math.abs(deltaY)) {
      if (Math.abs(deltaX) > minSwipeDistance) {
        if (deltaX > 0 && direction !== 'LEFT') setDirection('RIGHT');
        else if (deltaX < 0 && direction !== 'RIGHT') setDirection('LEFT');
      }
    } else {
      if (Math.abs(deltaY) > minSwipeDistance) {
        if (deltaY > 0 && direction !== 'UP') setDirection('DOWN');
        else if (deltaY < 0 && direction !== 'DOWN') setDirection('UP');
      }
    }
  };

  // Reset game
  const resetGame = () => {
    setSnake(INITIAL_SNAKE);
    setFood(generateFood()); 
    setDirection(INITIAL_DIRECTION);
    setScore(0);
    setIsGameOver(false);
    setIsPaused(false);
    setGameSpeed(INITIAL_SPEED);
    setHasShield(false);
    setScoreMultiplier(1);
    setCurrentThemeIndex(0); 
    setImagePopupVisible(false); 
    setCurrentRandomImageName(''); 
    setNextImageScoreThreshold(IMAGE_POPUP_SCORE_INTERVAL);
  };

  // Virtual controls
  const handleDirectionChange = (newDirection) => {
    if (isGameOver) return;
    const opposites = { 'UP': 'DOWN', 'DOWN': 'UP', 'LEFT': 'RIGHT', 'RIGHT': 'LEFT' };
    if (direction !== opposites[newDirection]) setDirection(newDirection);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-300 via-blue-400 to-indigo-500 p-2 sm:p-4 flex flex-col md:flex-row items-center md:items-start justify-center gap-4 md:gap-6 font-sans selection:bg-yellow-300 selection:text-yellow-900 overflow-x-hidden">
      {/* Game Area Column: Will use flex-1 on md+ screens to take available space */}
      <div className="w-full max-w-md md:flex-1 md:max-w-lg lg:max-w-xl xl:max-w-2xl">
        <div className="mx-auto bg-white/90 backdrop-blur-sm p-3 sm:p-4 rounded-3xl shadow-xl hover:shadow-pink-400/50 transition-shadow duration-500 ease-in-out animate-pulse-shadow-fun w-full relative overflow-hidden"> {/* Softer white, more rounded, fun shadow pulse */}
            {/* Header */}
            <div className="text-center mb-4 sm:mb-6">
            <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-orange-500 via-pink-500 to-red-500 mb-1 sm:mb-2 filter drop-shadow-sm">
                Snacke Adventure!
            </h1>
            <div className="flex justify-center items-center gap-3 sm:gap-4">
                <div className="text-lg sm:text-xl p-2.5 sm:p-3 bg-yellow-200/80 rounded-2xl shadow-lg text-yellow-700 font-semibold">Score: <span className="font-bold text-yellow-600">{score}</span></div>
                <div className="text-lg sm:text-xl p-2.5 sm:p-3 bg-purple-200/80 rounded-2xl shadow-lg text-purple-700 font-semibold">High: <span className="font-bold text-purple-600">{highScore}</span></div>
            </div>
            </div>

            {/* Status Indicators */}
            <div className="flex flex-wrap justify-center items-center gap-2 mb-3 sm:mb-4 h-auto min-h-[40px] sm:min-h-[48px]"> 
            {hasShield && (
                <div className={`flex items-center gap-1.5 ${POWER_UPS.SHIELD.color} ${POWER_UPS.SHIELD.textColor} px-3 py-1.5 rounded-full text-sm sm:text-md font-bold animate-bounce shadow-lg border-2 border-white/50`}>
                {POWER_UPS.SHIELD.icon} {POWER_UPS.SHIELD.description}
                </div>
            )}
            {scoreMultiplier > 1 && (
                <div className={`flex items-center gap-1.5 ${POWER_UPS.MULTIPLIER.color} ${POWER_UPS.MULTIPLIER.textColor} px-3 py-1.5 rounded-full text-sm sm:text-md font-bold animate-pulse shadow-lg border-2 border-white/50`}>
                {POWER_UPS.MULTIPLIER.icon} {POWER_UPS.MULTIPLIER.description}
                </div>
            )}
            {isPaused && !isGameOver && (
                <div className="flex items-center gap-1.5 bg-orange-300 text-orange-700 px-3 py-1.5 rounded-full text-sm sm:text-md font-bold shadow-md border-2 border-white/50">
                <Pause size={16} /> Paused Game
                </div>
            )}
            </div>

            {/* Game Board Container - merged with the grid element itself */} 
            <div 
                className="grid gap-px bg-yellow-100 border-2 sm:border-4 border-orange-300 rounded-2xl sm:rounded-3xl p-1 sm:p-1.5 aspect-square shadow-xl w-72 h-72 sm:w-96 sm:h-96 mx-auto" // Fixed size for grid container
                style={{ gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)` }}
                onTouchStart={handleTouchStart}
                onTouchEnd={handleTouchEnd}
            >
                {Array.from({ length: GRID_SIZE * GRID_SIZE }).map((_, index) => {
                const x = index % GRID_SIZE;
                const y = Math.floor(index / GRID_SIZE);
                const isSnakeHead = snake.length > 0 && snake[0].x === x && snake[0].y === y;
                const isSnakeBody = snake.slice(1).some(segment => segment.x === x && segment.y === y);
                const isFood = food.x === x && food.y === y;

                return (
                    <div
                    key={index}
                    className={`
                        aspect-square rounded-lg sm:rounded-xl transition-all duration-150 // More rounding
                        ${isSnakeHead ? `${currentSnakeColor.head} shadow-lg animate-pulse` : ''} 
                        ${isSnakeBody ? `${currentSnakeColor.body} opacity-95 shadow-sm` : ''} 
                        ${isFood ? 'flex items-center justify-center' : ''} 
                        ${!isSnakeHead && !isSnakeBody && !isFood ? 'bg-lime-50 hover:bg-lime-100/90' : ''} // Cheerful cell colors
                    `}
                    >
                    {isFood && (
                        <span className="text-3xl sm:text-4xl md:text-4xl flex items-center justify-center h-full w-full animate-bounce filter drop-shadow-lg hover:scale-110 transition-transform">
                        {POWER_UPS[food.type].icon}
                        </span>
                    )}
                    </div>
                );
                })}
            </div>

            {isGameOver && (
                <div className="absolute inset-0 bg-black/70 backdrop-blur-md flex items-center justify-center rounded-3xl z-20 p-4">
                <div className="text-center text-white bg-slate-700/90 p-6 sm:p-8 rounded-2xl shadow-xl ring-4 ring-pink-500/70">
                    <h2 className="text-4xl sm:text-5xl font-bold mb-3 sm:mb-4 text-red-400 filter drop-shadow-md">Game Over!</h2>
                    <p className="text-xl sm:text-2xl mb-4 sm:mb-6">Final Score: <span className="text-yellow-300 font-extrabold text-3xl">{score}</span></p>
                    <button
                    onClick={resetGame}
                    className="bg-gradient-to-br from-lime-400 to-green-500 hover:from-lime-500 hover:to-green-600 text-white px-8 sm:px-10 py-3 sm:py-4 rounded-full font-bold transition-all duration-300 ease-in-out transform hover:scale-110 shadow-xl text-lg sm:text-xl flex items-center gap-2.5 mx-auto ring-2 ring-white/80 hover:ring-white focus:ring-4 focus:ring-lime-300 outline-none"
                    >
                    <RotateCcw size={24} /> 
                    Play Again!
                    </button>
                </div>
                </div>
            )}

            {/* Controls */} 
            <div className="flex justify-center items-center gap-3 sm:gap-4 mt-4 sm:mt-5 mb-3 sm:mb-4">
                <button onClick={() => setIsPaused(!isPaused)} disabled={isGameOver} className="text-sm sm:text-md md:text-lg bg-gradient-to-br from-yellow-400 to-orange-500 hover:from-yellow-500 hover:to-orange-600 disabled:from-slate-300 disabled:to-slate-400 disabled:text-slate-500 disabled:opacity-70 text-white px-4 sm:px-5 py-2.5 sm:py-3 rounded-full font-bold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-lg flex items-center gap-1.5 sm:gap-2 ring-1 ring-black/20 hover:ring-white/60 focus:ring-4 focus:ring-yellow-300 outline-none">
                    {isPaused ? <Play size={20} /> : <Pause size={20} />}
                    {isPaused ? 'Resume' : 'Pause'}
                </button>
                <button onClick={resetGame} className="text-sm sm:text-md md:text-lg bg-gradient-to-br from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 disabled:from-slate-300 disabled:to-slate-400 disabled:text-slate-500 text-white px-4 sm:px-5 py-2.5 sm:py-3 rounded-full font-bold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-lg flex items-center gap-1.5 sm:gap-2 ring-1 ring-black/20 hover:ring-white/60 focus:ring-4 focus:ring-red-300 outline-none">
                    <RotateCcw size={20} /> Reset
                </button>
                <button onClick={() => setIsMuted(!isMuted)} className="text-sm sm:text-md md:text-lg bg-gradient-to-br from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white px-3 sm:px-4 py-2.5 sm:py-3 rounded-full font-bold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-lg flex items-center gap-1 sm:gap-1.5 ring-1 ring-black/20 hover:ring-white/60 focus:ring-4 focus:ring-purple-300 outline-none">
                    {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
                    <span className="sr-only">{isMuted ? 'Unmute' : 'Mute'}</span>
                </button>
            </div>

            {/* Virtual D-Pad for Mobile - more rounded and playful */}
            <div className="md:hidden">
                 <div className="grid grid-cols-3 gap-2 sm:gap-2.5 max-w-[200px] sm:max-w-[220px] mx-auto">
                    <div></div>
                    <button onClick={() => handleDirectionChange('UP')} className="bg-sky-400 hover:bg-sky-500 active:bg-sky-600 text-white p-3 sm:p-3.5 rounded-2xl flex items-center justify-center transition-colors aspect-square shadow-lg ring-1 ring-black/20 focus:ring-4 focus:ring-sky-300 outline-none"><ChevronUp size={28} /></button>
                    <div></div>
                    <button onClick={() => handleDirectionChange('LEFT')} className="bg-sky-400 hover:bg-sky-500 active:bg-sky-600 text-white p-3 sm:p-3.5 rounded-2xl flex items-center justify-center transition-colors aspect-square shadow-lg ring-1 ring-black/20 focus:ring-4 focus:ring-sky-300 outline-none"><ChevronLeft size={28} /></button>
                    <div className="flex items-center justify-center aspect-square"> 
                        <button onClick={() => setIsPaused(p => !p)} disabled={isGameOver} className="bg-yellow-400 hover:bg-yellow-500 active:bg-yellow-600 text-white p-3 sm:p-3.5 rounded-full flex items-center justify-center transition-colors h-14 w-14 sm:h-16 sm:w-16 shadow-lg ring-1 ring-black/20 focus:ring-4 focus:ring-yellow-300 outline-none">
                            {isPaused ? <Play size={22} /> : <Pause size={22} />} 
                        </button>
                    </div>
                    <button onClick={() => handleDirectionChange('RIGHT')} className="bg-sky-400 hover:bg-sky-500 active:bg-sky-600 text-white p-3 sm:p-3.5 rounded-2xl flex items-center justify-center transition-colors aspect-square shadow-lg ring-1 ring-black/20 focus:ring-4 focus:ring-sky-300 outline-none"><ChevronRight size={28} /></button>
                    <div></div>
                    <button onClick={() => handleDirectionChange('DOWN')} className="bg-sky-400 hover:bg-sky-500 active:bg-sky-600 text-white p-3 sm:p-3.5 rounded-2xl flex items-center justify-center transition-colors aspect-square shadow-lg ring-1 ring-black/20 focus:ring-4 focus:ring-sky-300 outline-none"><ChevronDown size={28} /></button>
                    <div></div>
                </div>
            </div>

            {/* Instructions - more playful font styling */}
            <div className="text-center text-slate-700 text-xs sm:text-sm mt-4 sm:mt-5 p-2.5 sm:p-3 bg-lime-100/70 rounded-xl shadow-inner">
                <p className="mb-1 font-medium">🎮 Use <b>Arrow Keys</b>, <b>WASD</b>, or <b>Swipe</b> to move!</p>
                <p className="mb-0.5 text-[11px] sm:text-xs leading-relaxed">
                    {POWER_UPS.PIZZA.icon}{POWER_UPS.HAMBURGER.icon}: Standard Food • 
                    {POWER_UPS.SPEED.icon} <span className="font-bold text-yellow-600">{POWER_UPS.SPEED.description}</span> • 
                    {POWER_UPS.MULTIPLIER.icon} <span className="font-bold text-pink-600">{POWER_UPS.MULTIPLIER.description}</span> • 
                    {POWER_UPS.SHIELD.icon} <span className="font-bold text-sky-600">{POWER_UPS.SHIELD.description}</span>
                </p>
                <p className="font-medium">Press <b>SPACE</b> to pause • Speed up every 5 points!</p>
            </div>
        </div>
      </div>

      {/* Side Panel for Barn Foto Snacke: more playful appearance */}
      {imagePopupVisible && NUM_BARN_FOTOS > 0 && (
        <div className="w-full max-w-xs sm:max-w-sm md:w-64 lg:w-72 xl:w-80 bg-gradient-to-br from-pink-200 via-purple-200 to-indigo-200 p-3 sm:p-4 rounded-3xl shadow-2xl flex flex-col items-center justify-start self-center md:self-start mt-4 md:mt-0 order-first ring-4 ring-white/80 flex-shrink-0 md:h-auto md:min-h-[calc(100vh-100px)] md:max-h-[calc(100vh-80px)] overflow-y-auto">
          <div className="bg-white/95 backdrop-blur-sm p-3 sm:p-4 rounded-2xl shadow-xl w-full transform transition-all sticky top-2 z-10 ring-2 ring-pink-300">
            {currentRandomImageName && (
              <img 
                key={currentRandomImageName} 
                src={`/barnfotosnake/${currentRandomImageName}`} 
                alt="Barn Foto Snacke Time!" 
                className="w-full h-auto rounded-xl object-contain max-h-[40vh] sm:max-h-[50vh] md:max-h-[calc(100vh-240px)] shadow-lg border-4 border-white hover:scale-105 transition-transform duration-300"
                onError={(e) => { 
                  e.target.style.display='none'; 
                  const parent = e.target.parentNode;
                  if (parent) {
                      const errorText = document.createElement('p');
                      errorText.textContent = `Oops! Image ${currentRandomImageName} not found.`;
                      errorText.className = 'text-red-500 font-semibold text-center p-2 sm:p-4';
                      if (!parent.querySelector('.error-image-text')) {
                        errorText.classList.add('error-image-text');
                        parent.insertBefore(errorText, e.target);
                      }
                  }
                }}
              />
            )}
            <p className="text-center text-2xl sm:text-3xl md:text-4xl font-extrabold mt-3 sm:mt-4 text-transparent bg-clip-text bg-gradient-to-r from-orange-400 via-red-400 to-pink-500 animate-bounce filter drop-shadow-lg">
                Snacke Time!
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SnakeGame;
