import React, { useState, useEffect, useCallback, useRef } from 'react';
import { ChevronUp, ChevronDown, ChevronLeft, ChevronRight, Play, Pause, RotateCcw, Volume2, VolumeX } from 'lucide-react';

const GRID_SIZE = 15;
const INITIAL_SNAKE = [{ x: 7, y: 7 }];
const INITIAL_DIRECTION = 'RIGHT';
const INITIAL_SPEED = 150;
const SPEED_INCREMENT = 0.1;

const POWER_UPS = {
  NORMAL: { color: 'bg-red-500', points: 1, probability: 0.7 },
  SPEED: { color: 'bg-green-500', points: 2, probability: 0.1 }, // e.g. 10%
  MULTIPLIER: { color: 'bg-yellow-500', points: 3, probability: 0.1 }, // e.g. 10%
  SHIELD: { color: 'bg-blue-500', points: 1, probability: 0.1 } // e.g. 10%
};

const SnakeGame = () => {
  const [snake, setSnake] = useState(INITIAL_SNAKE);
  const [food, setFood] = useState({ x: 10, y: 10, type: 'NORMAL' });
  const [direction, setDirection] = useState(INITIAL_DIRECTION);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('snakeHighScore');
    return saved ? parseInt(saved) : 0;
  });
  const [isGameOver, setIsGameOver] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [gameSpeed, setGameSpeed] = useState(INITIAL_SPEED);
  const [hasShield, setHasShield] = useState(false);
  const [scoreMultiplier, setScoreMultiplier] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [touchStart, setTouchStart] = useState({ x: 0, y: 0 });

  const gameLoopRef = useRef();
  const audioContextRef = useRef();

  // Initialize audio context
  useEffect(() => {
    if (!isMuted && !audioContextRef.current) {
      try {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      } catch (e) {
        console.log('Audio not supported');
      }
    }
  }, [isMuted]);

  // Play sound effect
  const playSound = useCallback((frequency, duration = 100) => {
    if (isMuted || !audioContextRef.current) return;
    
    try {
      const oscillator = audioContextRef.current.createOscillator();
      const gainNode = audioContextRef.current.createGain();
      
      oscillator.connect(gainNode);
      gainNode.connect(audioContextRef.current.destination);
      
      oscillator.frequency.setValueAtTime(frequency, audioContextRef.current.currentTime);
      oscillator.type = 'square';
      
      gainNode.gain.setValueAtTime(0.1, audioContextRef.current.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContextRef.current.currentTime + duration / 1000);
      
      oscillator.start(audioContextRef.current.currentTime);
      oscillator.stop(audioContextRef.current.currentTime + duration / 1000);
    } catch (e) {
      console.log('Audio playback failed');
    }
  }, [isMuted]);

  // Generate random food position
  const generateFood = useCallback(() => {
    let newFoodPosition;
    do {
      newFoodPosition = {
        x: Math.floor(Math.random() * GRID_SIZE),
        y: Math.floor(Math.random() * GRID_SIZE),
      };
    } while (snake.some(segment => segment.x === newFoodPosition.x && segment.y === newFoodPosition.y));

    const rand = Math.random();
    let cumulativeProbability = 0;
    let foodType = 'NORMAL'; // Default to normal

    for (const type in POWER_UPS) {
        cumulativeProbability += POWER_UPS[type].probability;
        if (rand < cumulativeProbability) {
            foodType = type;
            break;
        }
    }
    
    return {...newFoodPosition, type: foodType};
  }, [snake]);

  // Handle food consumption
  const handleFoodConsumption = useCallback((foodType) => {
    const basePoints = POWER_UPS[foodType].points;
    const points = basePoints * scoreMultiplier;
    
    setScore(prev => prev + points);
    playSound(440, 150);
    
    switch (foodType) {
      case 'SPEED':
        setGameSpeed(prev => Math.max(50, prev * 0.8));
        setTimeout(() => setGameSpeed(prev => prev / 0.8), 5000);
        break;
      case 'MULTIPLIER':
        setScoreMultiplier(2);
        setTimeout(() => setScoreMultiplier(1), 8000);
        break;
      case 'SHIELD':
        setHasShield(true);
        setTimeout(() => setHasShield(false), 10000);
        break;
    }
  }, [scoreMultiplier, playSound]);

  // Move snake
  const moveSnake = useCallback(() => {
    if (isPaused || isGameOver) return;

    setSnake(currentSnake => {
      const head = { ...currentSnake[0] };
      
      switch (direction) {
        case 'UP': head.y -= 1; break;
        case 'DOWN': head.y += 1; break;
        case 'LEFT': head.x -= 1; break;
        case 'RIGHT': head.x += 1; break;
      }

      // Wall collision
      if (head.x < 0 || head.x >= GRID_SIZE || head.y < 0 || head.y >= GRID_SIZE) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      // Self collision
      if (currentSnake.some(segment => segment.x === head.x && segment.y === head.y)) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      const newSnake = [head, ...currentSnake];

      // Food collision
      if (head.x === food.x && head.y === food.y) {
        handleFoodConsumption(food.type);
        setFood(generateFood());
        return newSnake;
      }

      return newSnake.slice(0, -1);
    });
  }, [direction, isPaused, isGameOver, food, hasShield, handleFoodConsumption, generateFood, playSound]);

  // Game loop
  useEffect(() => {
    if (!isGameOver && !isPaused) {
      gameLoopRef.current = setTimeout(moveSnake, gameSpeed);
    }
    return () => clearTimeout(gameLoopRef.current);
  }, [moveSnake, gameSpeed, isGameOver, isPaused]);

  // Update high score
  useEffect(() => {
    if (score > highScore) {
      setHighScore(score);
      localStorage.setItem('snakeHighScore', score.toString());
    }
  }, [score, highScore]);

  // Increase speed based on score
  useEffect(() => {
    const newSpeed = Math.max(50, INITIAL_SPEED - Math.floor(score / 5) * (INITIAL_SPEED * SPEED_INCREMENT));
    setGameSpeed(newSpeed);
  }, [score]);

  // Keyboard controls
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (isGameOver) return;
      
      switch (e.key) {
        case 'ArrowUp':
        case 'w':
        case 'W':
          e.preventDefault();
          if (direction !== 'DOWN') setDirection('UP');
          break;
        case 'ArrowDown':
        case 's':
        case 'S':
          e.preventDefault();
          if (direction !== 'UP') setDirection('DOWN');
          break;
        case 'ArrowLeft':
        case 'a':
        case 'A':
          e.preventDefault();
          if (direction !== 'RIGHT') setDirection('LEFT');
          break;
        case 'ArrowRight':
        case 'd':
        case 'D':
          e.preventDefault();
          if (direction !== 'LEFT') setDirection('RIGHT');
          break;
        case ' ':
          e.preventDefault();
          setIsPaused(prev => !prev);
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [direction, isGameOver]);

  // Touch controls
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setTouchStart({ x: touch.clientX, y: touch.clientY });
  };

  const handleTouchEnd = (e) => {
    if (isGameOver) return;
    
    const touch = e.changedTouches[0];
    const deltaX = touch.clientX - touchStart.x;
    const deltaY = touch.clientY - touchStart.y;
    const minSwipeDistance = 30; // Reduced for better responsiveness

    if (Math.abs(deltaX) > Math.abs(deltaY)) { // Horizontal swipe
      if (Math.abs(deltaX) > minSwipeDistance) {
        if (deltaX > 0 && direction !== 'LEFT') setDirection('RIGHT');
        else if (deltaX < 0 && direction !== 'RIGHT') setDirection('LEFT');
      }
    } else { // Vertical swipe
      if (Math.abs(deltaY) > minSwipeDistance) {
        if (deltaY > 0 && direction !== 'UP') setDirection('DOWN');
        else if (deltaY < 0 && direction !== 'DOWN') setDirection('UP');
      }
    }
  };

  // Reset game
  const resetGame = () => {
    setSnake(INITIAL_SNAKE);
    setFood(generateFood()); // Use generateFood to get a random initial food
    setDirection(INITIAL_DIRECTION);
    setScore(0);
    setIsGameOver(false);
    setIsPaused(false);
    setGameSpeed(INITIAL_SPEED);
    setHasShield(false);
    setScoreMultiplier(1);
  };

  // Virtual controls
  const handleDirectionChange = (newDirection) => {
    if (isGameOver) return;
    
    const opposites = {
      'UP': 'DOWN',
      'DOWN': 'UP',
      'LEFT': 'RIGHT',
      'RIGHT': 'LEFT'
    };
    
    if (direction !== opposites[newDirection]) {
      setDirection(newDirection);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 p-4 flex flex-col items-center justify-center">
      <div className="max-w-2xl w-full">
        {/* Header */}
        <div className="text-center mb-6">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-2 bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">
            Snake Game
          </h1>
          <div className="flex justify-center items-center gap-6 text-white">
            <div className="text-lg">Score: <span className="font-bold text-yellow-400">{score}</span></div>
            <div className="text-lg">High: <span className="font-bold text-purple-400">{highScore}</span></div>
          </div>
        </div>

        {/* Status Indicators */}
        <div className="flex justify-center items-center gap-4 mb-4 h-8"> {/* Added fixed height */}
          {hasShield && (
            <div className="bg-blue-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
              üõ°Ô∏è Shield Active
            </div>
          )}
          {scoreMultiplier > 1 && (
            <div className="bg-yellow-500 text-white px-3 py-1 rounded-full text-sm animate-pulse">
              ‚ö° 2x Multiplier
            </div>
          )}
          {isPaused && !isGameOver && ( // Only show if not game over
            <div className="bg-orange-500 text-white px-3 py-1 rounded-full text-sm">
              ‚è∏Ô∏è Paused
            </div>
          )}
        </div>

        {/* Game Board */}
        <div className="relative">
          <div 
            className="grid gap-0 bg-gray-800 border-2 border-gray-600 rounded-lg p-2 mb-4 aspect-square max-w-md mx-auto"
            style={{ gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)` }}
            onTouchStart={handleTouchStart}
            onTouchEnd={handleTouchEnd}
          >
            {Array.from({ length: GRID_SIZE * GRID_SIZE }).map((_, index) => {
              const x = index % GRID_SIZE;
              const y = Math.floor(index / GRID_SIZE);
              const isSnakeHead = snake.length > 0 && snake[0].x === x && snake[0].y === y;
              const isSnakeBody = snake.slice(1).some(segment => segment.x === x && segment.y === y);
              const isFood = food.x === x && food.y === y;

              return (
                <div
                  key={index}
                  className={`
                    aspect-square rounded-sm transition-all duration-150
                    ${isSnakeHead ? 'bg-gradient-to-br from-indigo-400 to-indigo-600 animate-pulse shadow-lg' : ''}
                    ${isSnakeBody ? 'bg-gradient-to-br from-indigo-500 to-indigo-700 transform scale-95' : ''}
                    ${isFood ? `${POWER_UPS[food.type].color} animate-pulse shadow-lg rounded-full` : ''}
                    ${!isSnakeHead && !isSnakeBody && !isFood ? 'bg-gray-700' : ''}
                  `}
                />
              );
            })}
          </div>

          {/* Game Over Overlay */}
          {isGameOver && (
            <div className="absolute inset-0 bg-black bg-opacity-75 flex items-center justify-center rounded-lg">
              <div className="text-center text-white">
                <h2 className="text-3xl font-bold mb-4">Game Over!</h2>
                <p className="text-xl mb-4">Final Score: {score}</p>
                <button
                  onClick={resetGame}
                  className="bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-lg font-semibold transition-colors flex items-center gap-2 mx-auto"
                >
                  <RotateCcw size={20} />
                  Play Again
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Controls */}
        <div className="flex justify-center items-center gap-4 mb-6">
          <button
            onClick={() => setIsPaused(!isPaused)}
            disabled={isGameOver}
            className="bg-green-600 hover:bg-green-700 disabled:bg-gray-600 text-white px-4 py-2 rounded-lg font-semibold transition-colors flex items-center gap-2"
          >
            {isPaused ? <Play size={20} /> : <Pause size={20} />}
            {isPaused ? 'Resume' : 'Pause'}
          </button>
          
          <button
            onClick={resetGame}
            className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg font-semibold transition-colors flex items-center gap-2"
          >
            <RotateCcw size={20} />
            Reset
          </button>

          <button
            onClick={() => setIsMuted(!isMuted)}
            className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg font-semibold transition-colors flex items-center gap-2"
          >
            {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
          </button>
        </div>

        {/* Virtual D-Pad for Mobile */}
        <div className="md:hidden">
          <div className="grid grid-cols-3 gap-2 max-w-xs mx-auto"> {/* Adjusted max-w for better fit */}
            <div></div>
            <button
              onClick={() => handleDirectionChange('UP')}
              className="bg-gray-700 hover:bg-gray-600 text-white p-4 rounded-lg flex items-center justify-center transition-colors active:bg-gray-500 aspect-square"
            >
              <ChevronUp size={24} />
            </button>
            <div></div>
            <button
              onClick={() => handleDirectionChange('LEFT')}
              className="bg-gray-700 hover:bg-gray-600 text-white p-4 rounded-lg flex items-center justify-center transition-colors active:bg-gray-500 aspect-square"
            >
              <ChevronLeft size={24} />
            </button>
             <div className="flex items-center justify-center aspect-square"> 
                <button 
                    onClick={() => setIsPaused(p => !p)} /* Corrected setIsPaused */
                    disabled={isGameOver}
                    className="bg-gray-700 hover:bg-gray-600 text-white p-3 rounded-full flex items-center justify-center transition-colors active:bg-gray-500 h-12 w-12" /* Adjusted size */
                >
                    {isPaused ? <Play size={18} /> : <Pause size={18} />}
                </button>
            </div>
            <button
              onClick={() => handleDirectionChange('RIGHT')}
              className="bg-gray-700 hover:bg-gray-600 text-white p-4 rounded-lg flex items-center justify-center transition-colors active:bg-gray-500 aspect-square"
            >
              <ChevronRight size={24} />
            </button>
            <div></div>
            <button
              onClick={() => handleDirectionChange('DOWN')}
              className="bg-gray-700 hover:bg-gray-600 text-white p-4 rounded-lg flex items-center justify-center transition-colors active:bg-gray-500 aspect-square"
            >
              <ChevronDown size={24} />
            </button>
            <div></div>
          </div>
        </div>

        {/* Instructions */}
        <div className="text-center text-gray-400 text-sm mt-8"> {/* Increased mt */}
          <p className="mb-1">üéÆ Use arrow keys, WASD, or swipe to move</p>
          <p className="mb-1">üçé <span className="text-red-400">Red</span>: Normal | üçÄ <span className="text-green-400">Green</span>: Speed | ‚≠ê <span className="text-yellow-400">Yellow</span>: 2x Score | üõ°Ô∏è <span className="text-blue-400">Blue</span>: Shield</p>
          <p>Press SPACE to pause ‚Ä¢ Speed increases every 5 points</p>
        </div>
      </div>
    </div>
  );
};

export default SnakeGame;
