import React, { useState, useEffect, useCallback, useRef } from 'react';
import { ChevronUp, ChevronDown, ChevronLeft, ChevronRight, Play, Pause, RotateCcw, Volume2, VolumeX } from 'lucide-react';

const GRID_SIZE = 15;
const INITIAL_SNAKE = [{ x: 7, y: 7 }];
const INITIAL_DIRECTION = 'RIGHT';
const INITIAL_SPEED = 150;
const SPEED_INCREMENT = 0.1;

// Barn Foto Snacke Feature Constants
const IMAGE_POPUP_SCORE_INTERVAL = 15;
const NUM_BARN_FOTOS = 21; 
const IMAGE_POPUP_DURATION = 3000; // Total duration the panel is shown

const SNAKE_COLOR_THEMES = [
  { head: 'bg-gradient-to-br from-lime-400 to-green-500', body: 'bg-gradient-to-br from-lime-500 to-green-600' },
  { head: 'bg-gradient-to-br from-pink-400 to-red-500', body: 'bg-gradient-to-br from-pink-500 to-red-600' },
  { head: 'bg-gradient-to-br from-yellow-300 to-orange-400', body: 'bg-gradient-to-br from-yellow-400 to-orange-500' },
  { head: 'bg-gradient-to-br from-cyan-300 to-blue-400', body: 'bg-gradient-to-br from-cyan-400 to-blue-500' },
  { head: 'bg-gradient-to-br from-purple-400 to-indigo-500', body: 'bg-gradient-to-br from-purple-500 to-indigo-600' },
];

const POWER_UPS = {
  PIZZA: { icon: '🍕', points: 1, probability: 0.35, description: "Pizza Slice!" },
  HAMBURGER: { icon: '🍔', points: 1, probability: 0.35, description: "Yummy Burger!" },
  SPEED: { icon: '⚡️', points: 2, probability: 0.1, description: "Zoom Zoom!", color: 'bg-yellow-400', textColor: 'text-yellow-800' },
  MULTIPLIER: { icon: '✨', points: 3, probability: 0.1, description: "Super Score!", color: 'bg-pink-400', textColor: 'text-pink-800' },
  SHIELD: { icon: '🛡️', points: 1, probability: 0.1, description: "Safe Shield!", color: 'bg-sky-400', textColor: 'text-sky-800' }
};

const getRandomFoodType = () => {
  const rand = Math.random();
  let cumulativeProbability = 0;
  for (const type in POWER_UPS) {
    cumulativeProbability += POWER_UPS[type].probability;
    if (rand < cumulativeProbability) {
      return type;
    }
  }
  return 'PIZZA'; 
};

const getRandomImageName = () => { 
  if (NUM_BARN_FOTOS === 0) return '';
  const randomIndex = Math.floor(Math.random() * NUM_BARN_FOTOS) + 1;
  return `${randomIndex}.jpg`;
};

const SnakeGame = () => {
  const [snake, setSnake] = useState(INITIAL_SNAKE);
  const [food, setFood] = useState({ x: 10, y: 10, type: getRandomFoodType() });
  const [direction, setDirection] = useState(INITIAL_DIRECTION);
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(() => {
    const saved = localStorage.getItem('snakeHighScore');
    return saved ? parseInt(saved) : 0;
  });
  const [isGameOver, setIsGameOver] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [gameSpeed, setGameSpeed] = useState(INITIAL_SPEED);
  const [hasShield, setHasShield] = useState(false);
  const [scoreMultiplier, setScoreMultiplier] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [touchStart, setTouchStart] = useState({ x: 0, y: 0 });
  const [currentThemeIndex, setCurrentThemeIndex] = useState(0);

  const [imagePopupVisible, setImagePopupVisible] = useState(false); 
  const [currentRandomImageName, setCurrentRandomImageName] = useState('');
  const [nextImageScoreThreshold, setNextImageScoreThreshold] = useState(IMAGE_POPUP_SCORE_INTERVAL);

  const gameLoopRef = useRef();
  const audioContextRef = useRef();
  const currentSnakeColor = SNAKE_COLOR_THEMES[currentThemeIndex];

  // Initialize audio context
  useEffect(() => {
    if (!isMuted && !audioContextRef.current) {
      try {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      } catch (e) {
        console.log('Audio not supported');
      }
    }
  }, [isMuted]);

  // Play sound effect
  const playSound = useCallback((frequency, duration = 100) => {
    if (isMuted || !audioContextRef.current) return;
    
    try {
      const oscillator = audioContextRef.current.createOscillator();
      const gainNode = audioContextRef.current.createGain();
      
      oscillator.connect(gainNode);
      gainNode.connect(audioContextRef.current.destination);
      
      oscillator.frequency.setValueAtTime(frequency, audioContextRef.current.currentTime);
      oscillator.type = 'square';
      
      gainNode.gain.setValueAtTime(0.1, audioContextRef.current.currentTime);
      gainNode.gain.exponentialRampToValueAtTime(0.01, audioContextRef.current.currentTime + duration / 1000);
      
      oscillator.start(audioContextRef.current.currentTime);
      oscillator.stop(audioContextRef.current.currentTime + duration / 1000);
    } catch (e) {
      console.log('Audio playback failed');
    }
  }, [isMuted]);

  // Generate random food position
  const generateFood = useCallback(() => {
    let newFoodPosition;
    do {
      newFoodPosition = {
        x: Math.floor(Math.random() * GRID_SIZE),
        y: Math.floor(Math.random() * GRID_SIZE),
      };
    } while (snake.some(segment => segment.x === newFoodPosition.x && segment.y === newFoodPosition.y));
    
    return {...newFoodPosition, type: getRandomFoodType()};
  }, [snake]);

  // Handle food consumption
  const handleFoodConsumption = useCallback((foodType) => {
    const basePoints = POWER_UPS[foodType].points;
    const points = basePoints * scoreMultiplier;
    
    setScore(prev => prev + points);
    playSound(440, 150);
    setCurrentThemeIndex(prev => (prev + 1) % SNAKE_COLOR_THEMES.length);
    
    switch (foodType) {
      case 'SPEED':
        setGameSpeed(prev => Math.max(50, prev * 0.8));
        setTimeout(() => setGameSpeed(prev => prev / 0.8), 5000);
        break;
      case 'MULTIPLIER':
        setScoreMultiplier(2);
        setTimeout(() => setScoreMultiplier(1), 8000);
        break;
      case 'SHIELD':
        setHasShield(true);
        setTimeout(() => setHasShield(false), 10000);
        break;
    }
  }, [scoreMultiplier, playSound]);

  // Move snake
  const moveSnake = useCallback(() => {
    if (isPaused || isGameOver) return;

    setSnake(currentSnake => {
      const head = { ...currentSnake[0] };
      
      switch (direction) {
        case 'UP': head.y -= 1; break;
        case 'DOWN': head.y += 1; break;
        case 'LEFT': head.x -= 1; break;
        case 'RIGHT': head.x += 1; break;
      }

      if (head.x < 0 || head.x >= GRID_SIZE || head.y < 0 || head.y >= GRID_SIZE) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      if (currentSnake.some(segment => segment.x === head.x && segment.y === head.y)) {
        if (hasShield) {
          setHasShield(false);
          playSound(200, 300);
          return currentSnake;
        } else {
          setIsGameOver(true);
          playSound(150, 500);
          return currentSnake;
        }
      }

      const newSnake = [head, ...currentSnake];

      if (head.x === food.x && head.y === food.y) {
        handleFoodConsumption(food.type);
        setFood(generateFood());
        return newSnake;
      }

      return newSnake.slice(0, -1);
    });
  }, [direction, isPaused, isGameOver, food, hasShield, handleFoodConsumption, generateFood, playSound]);

  // Game loop
  useEffect(() => {
    if (!isGameOver && !isPaused) {
      gameLoopRef.current = setTimeout(moveSnake, gameSpeed);
    }
    return () => clearTimeout(gameLoopRef.current);
  }, [moveSnake, gameSpeed, isGameOver, isPaused]);

  // Update high score & Barn Foto Snacke popup trigger
  useEffect(() => {
    if (score > highScore) {
      setHighScore(score);
      localStorage.setItem('snakeHighScore', score.toString());
    }

    if (!imagePopupVisible && score > 0 && score >= nextImageScoreThreshold && NUM_BARN_FOTOS > 0) {
      setImagePopupVisible(true);
      setCurrentRandomImageName(getRandomImageName()); // Set a single random image for the popup session
      playSound(660, 200); 
      setNextImageScoreThreshold(prevThreshold => prevThreshold + IMAGE_POPUP_SCORE_INTERVAL);
      
      setTimeout(() => {
        setImagePopupVisible(false);
      }, IMAGE_POPUP_DURATION);
    }
  }, [score, highScore, nextImageScoreThreshold, imagePopupVisible, playSound]);

  // Increase speed based on score
  useEffect(() => {
    const newSpeed = Math.max(50, INITIAL_SPEED - Math.floor(score / 5) * (INITIAL_SPEED * SPEED_INCREMENT));
    setGameSpeed(newSpeed);
  }, [score]);

  // Keyboard controls
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (isGameOver) return;
      
      switch (e.key) {
        case 'ArrowUp': case 'w': case 'W':
          e.preventDefault(); if (direction !== 'DOWN') setDirection('UP'); break;
        case 'ArrowDown': case 's': case 'S':
          e.preventDefault(); if (direction !== 'UP') setDirection('DOWN'); break;
        case 'ArrowLeft': case 'a': case 'A':
          e.preventDefault(); if (direction !== 'RIGHT') setDirection('LEFT'); break;
        case 'ArrowRight': case 'd': case 'D':
          e.preventDefault(); if (direction !== 'LEFT') setDirection('RIGHT'); break;
        case ' ':
          e.preventDefault(); setIsPaused(prev => !prev); break;
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [direction, isGameOver]);

  // Touch controls
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setTouchStart({ x: touch.clientX, y: touch.clientY });
  };

  const handleTouchEnd = (e) => {
    if (isGameOver) return;
    const touch = e.changedTouches[0];
    const deltaX = touch.clientX - touchStart.x;
    const deltaY = touch.clientY - touchStart.y;
    const minSwipeDistance = 30;
    if (Math.abs(deltaX) > Math.abs(deltaY)) {
      if (Math.abs(deltaX) > minSwipeDistance) {
        if (deltaX > 0 && direction !== 'LEFT') setDirection('RIGHT');
        else if (deltaX < 0 && direction !== 'RIGHT') setDirection('LEFT');
      }
    } else {
      if (Math.abs(deltaY) > minSwipeDistance) {
        if (deltaY > 0 && direction !== 'UP') setDirection('DOWN');
        else if (deltaY < 0 && direction !== 'DOWN') setDirection('UP');
      }
    }
  };

  // Reset game
  const resetGame = () => {
    setSnake(INITIAL_SNAKE);
    setFood(generateFood()); 
    setDirection(INITIAL_DIRECTION);
    setScore(0);
    setIsGameOver(false);
    setIsPaused(false);
    setGameSpeed(INITIAL_SPEED);
    setHasShield(false);
    setScoreMultiplier(1);
    setCurrentThemeIndex(0); 
    setImagePopupVisible(false); 
    setCurrentRandomImageName(''); 
    setNextImageScoreThreshold(IMAGE_POPUP_SCORE_INTERVAL);
  };

  // Virtual controls
  const handleDirectionChange = (newDirection) => {
    if (isGameOver) return;
    const opposites = { 'UP': 'DOWN', 'DOWN': 'UP', 'LEFT': 'RIGHT', 'RIGHT': 'LEFT' };
    if (direction !== opposites[newDirection]) setDirection(newDirection);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-300 via-blue-400 to-indigo-500 p-4 flex flex-col md:flex-row items-center md:items-start justify-center gap-6 md:gap-10 font-sans selection:bg-yellow-300 selection:text-yellow-900">
      {/* Game Area Column */}
      <div className="w-full md:max-w-lg lg:max-w-xl flex-shrink-0">
        <div className="mx-auto bg-white/80 backdrop-blur-sm p-4 sm:p-6 rounded-2xl shadow-2xl">
            {/* Header */}
            <div className="text-center mb-6">
            <h1 className="text-5xl md:text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-600 via-pink-500 to-red-500 mb-2">
                Snacke Adventure!
            </h1>
            <div className="flex justify-center items-center gap-4 text-slate-700">
                <div className="text-xl p-2 bg-yellow-300 rounded-lg shadow-md">Score: <span className="font-bold text-yellow-700">{score}</span></div>
                <div className="text-xl p-2 bg-purple-300 rounded-lg shadow-md">High: <span className="font-bold text-purple-700">{highScore}</span></div>
            </div>
            </div>

            {/* Status Indicators */}
            <div className="flex justify-center items-center gap-3 mb-4 h-10">
            {hasShield && (
                <div className={`flex items-center gap-2 ${POWER_UPS.SHIELD.color} ${POWER_UPS.SHIELD.textColor} px-3 py-1.5 rounded-full text-sm font-semibold animate-bounce shadow-lg`}>
                {POWER_UPS.SHIELD.icon} {POWER_UPS.SHIELD.description}
                </div>
            )}
            {scoreMultiplier > 1 && (
                <div className={`flex items-center gap-2 ${POWER_UPS.MULTIPLIER.color} ${POWER_UPS.MULTIPLIER.textColor} px-3 py-1.5 rounded-full text-sm font-semibold animate-pulse shadow-lg`}>
                {POWER_UPS.MULTIPLIER.icon} {POWER_UPS.MULTIPLIER.description}
                </div>
            )}
            {isPaused && !isGameOver && (
                <div className="flex items-center gap-2 bg-orange-400 text-orange-800 px-3 py-1.5 rounded-full text-sm font-semibold shadow-md">
                <Pause size={16} /> Paused Game
                </div>
            )}
            </div>

            {/* Game Board */}
            <div className="relative">
            <div 
                className="grid gap-0.5 bg-slate-300 border-4 border-slate-400 rounded-xl p-1.5 mb-4 aspect-square max-w-md mx-auto shadow-inner"
                style={{ gridTemplateColumns: `repeat(${GRID_SIZE}, 1fr)` }}
                onTouchStart={handleTouchStart}
                onTouchEnd={handleTouchEnd}
            >
                {Array.from({ length: GRID_SIZE * GRID_SIZE }).map((_, index) => {
                const x = index % GRID_SIZE;
                const y = Math.floor(index / GRID_SIZE);
                const isSnakeHead = snake.length > 0 && snake[0].x === x && snake[0].y === y;
                const isSnakeBody = snake.slice(1).some(segment => segment.x === x && segment.y === y);
                const isFood = food.x === x && food.y === y;

                return (
                    <div
                    key={index}
                    className={`
                        aspect-square rounded-md transition-all duration-100
                        ${isSnakeHead ? `${currentSnakeColor.head} shadow-lg ring-2 ring-white/50 animate-pulse` : ''}
                        ${isSnakeBody ? `${currentSnakeColor.body} opacity-90` : ''}
                        ${isFood ? 'flex items-center justify-center' : ''} 
                        ${!isSnakeHead && !isSnakeBody && !isFood ? 'bg-slate-200 hover:bg-slate-300/70' : ''}
                    `}
                    >
                    {isFood && (
                        <span className="text-3xl md:text-4xl flex items-center justify-center h-full w-full animate-bounce filter drop-shadow-lg">
                        {POWER_UPS[food.type].icon}
                        </span>
                    )}
                    </div>
                );
                })}
            </div>

            {/* Game Over Overlay - remains relative to game board area */}
            {isGameOver && (
                <div className="absolute inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center rounded-xl z-20">
                <div className="text-center text-white bg-slate-700/80 p-8 rounded-2xl shadow-xl">
                    <h2 className="text-4xl font-bold mb-4 text-red-400">Game Over!</h2>
                    <p className="text-2xl mb-6">Final Score: <span className="text-yellow-300 font-bold">{score}</span></p>
                    <button
                    onClick={resetGame}
                    className="bg-gradient-to-r from-green-400 to-blue-500 hover:from-green-500 hover:to-blue-600 text-white px-8 py-3 rounded-full font-semibold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-lg text-lg flex items-center gap-2 mx-auto ring-2 ring-white/50 hover:ring-white"
                    >
                    <RotateCcw size={22} />
                    Play Again!
                    </button>
                </div>
                </div>
            )}
            </div>

            {/* Controls */}
            <div className="flex justify-center items-center gap-3 md:gap-4 mb-6">
                <button onClick={() => setIsPaused(!isPaused)} disabled={isGameOver} className="bg-gradient-to-r from-yellow-400 to-orange-500 hover:from-yellow-500 hover:to-orange-600 disabled:from-slate-400 disabled:to-slate-500 disabled:opacity-70 text-white px-5 py-2.5 rounded-full font-semibold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-md text-md flex items-center gap-2 ring-1 ring-black/10 hover:ring-white/50">
                    {isPaused ? <Play size={18} /> : <Pause size={18} />}
                    {isPaused ? 'Resume' : 'Pause'}
                </button>
                <button onClick={resetGame} className="bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white px-5 py-2.5 rounded-full font-semibold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-md text-md flex items-center gap-2 ring-1 ring-black/10 hover:ring-white/50">
                    <RotateCcw size={18} /> Reset
                </button>
                <button onClick={() => setIsMuted(!isMuted)} className="bg-gradient-to-r from-purple-500 to-indigo-500 hover:from-purple-600 hover:to-indigo-600 text-white px-4 py-2.5 rounded-full font-semibold transition-all duration-300 ease-in-out transform hover:scale-105 shadow-md text-md flex items-center gap-1.5 ring-1 ring-black/10 hover:ring-white/50">
                    {isMuted ? <VolumeX size={18} /> : <Volume2 size={18} />}
                    <span className="sr-only">{isMuted ? 'Unmute' : 'Mute'}</span>
                </button>
            </div>

            {/* Virtual D-Pad for Mobile */}
            <div className="md:hidden">
                 <div className="grid grid-cols-3 gap-2 max-w-[200px] mx-auto">
                    <div></div>
                    <button onClick={() => handleDirectionChange('UP')} className="bg-slate-600 hover:bg-slate-500 active:bg-slate-700 text-white p-3 rounded-xl flex items-center justify-center transition-colors aspect-square shadow-md ring-1 ring-black/20"><ChevronUp size={28} /></button>
                    <div></div>
                    <button onClick={() => handleDirectionChange('LEFT')} className="bg-slate-600 hover:bg-slate-500 active:bg-slate-700 text-white p-3 rounded-xl flex items-center justify-center transition-colors aspect-square shadow-md ring-1 ring-black/20"><ChevronLeft size={28} /></button>
                    <div className="flex items-center justify-center aspect-square"> 
                        <button onClick={() => setIsPaused(p => !p)} disabled={isGameOver} className="bg-slate-600 hover:bg-slate-500 active:bg-slate-700 text-white p-2.5 rounded-full flex items-center justify-center transition-colors h-14 w-14 shadow-md ring-1 ring-black/20">
                            {isPaused ? <Play size={20} /> : <Pause size={20} />}
                        </button>
                    </div>
                    <button onClick={() => handleDirectionChange('RIGHT')} className="bg-slate-600 hover:bg-slate-500 active:bg-slate-700 text-white p-3 rounded-xl flex items-center justify-center transition-colors aspect-square shadow-md ring-1 ring-black/20"><ChevronRight size={28} /></button>
                    <div></div>
                    <button onClick={() => handleDirectionChange('DOWN')} className="bg-slate-600 hover:bg-slate-500 active:bg-slate-700 text-white p-3 rounded-xl flex items-center justify-center transition-colors aspect-square shadow-md ring-1 ring-black/20"><ChevronDown size={28} /></button>
                    <div></div>
                </div>
            </div>

            {/* Instructions */}
            <div className="text-center text-slate-600 text-xs sm:text-sm mt-6 p-2 bg-slate-100/70 rounded-lg">
                <p className="mb-1">🎮 Use <b>Arrow Keys</b>, <b>WASD</b>, or <b>Swipe</b> to move!</p>
                <p className="mb-0.5">
                    {POWER_UPS.PIZZA.icon} {POWER_UPS.HAMBURGER.icon}: Standard Food | {POWER_UPS.SPEED.icon} <span className="font-semibold text-yellow-700">{POWER_UPS.SPEED.description}</span> | 
                    {POWER_UPS.MULTIPLIER.icon} <span className="font-semibold text-pink-700">{POWER_UPS.MULTIPLIER.description}</span> | {POWER_UPS.SHIELD.icon} <span className="font-semibold text-sky-700">{POWER_UPS.SHIELD.description}</span>
                </p>
                <p>Press <b>SPACE</b> to pause • Speed increases every 5 points!</p>
            </div>
        </div>
      </div>

      {/* Side Panel for Barn Foto Snacke (conditionally rendered) */}
      {imagePopupVisible && NUM_BARN_FOTOS > 0 && (
        <div className="w-full md:w-72 lg:w-80 xl:w-96 bg-gradient-to-br from-pink-300 via-purple-300 to-indigo-300 p-4 rounded-2xl shadow-2xl flex flex-col items-center justify-center self-center md:self-start mt-6 md:mt-0 order-first md:order-last ring-4 ring-white/70">
          <div className="bg-white/90 backdrop-blur-sm p-3 md:p-4 rounded-xl shadow-xl w-full transform transition-all">
            {currentRandomImageName && (
              <img 
                key={currentRandomImageName} 
                src={`/barnfotosnake/${currentRandomImageName}`} 
                alt="Barn Foto Snacke Time!" 
                className="w-full h-auto rounded-lg object-contain max-h-[50vh] md:max-h-[calc(100vh-300px)] shadow-lg border-4 border-white"
                onError={(e) => { 
                  e.target.style.display='none'; 
                  const parent = e.target.parentNode;
                  if (parent) {
                      const errorText = document.createElement('p');
                      errorText.textContent = `Oops! Image ${currentRandomImageName} not found.`;
                      errorText.className = 'text-red-600 font-semibold text-center p-4';
                      if (!parent.querySelector('.error-image-text')) {
                        errorText.classList.add('error-image-text');
                        parent.insertBefore(errorText, e.target);
                      }
                  }
                }}
              />
            )}
            <p className="text-center text-2xl md:text-3xl font-extrabold mt-4 text-transparent bg-clip-text bg-gradient-to-r from-orange-500 via-red-500 to-pink-600 animate-bounce filter drop-shadow-md">
                Snacke Time!
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SnakeGame;
